    )
*/
const [num,setnum]=useState(0);
const [nums,setnums]=useState(0);
useEffect(()=>{
    alert("i am clicked");
})
return(
    <>
    <button onClick={()=>{
        setnum(num+1)
    }}>Click me {num}</button>
    <br/>
    <button onClick={()=>{
        setnums(nums+1)
    }}>Click me {nums}</button>
     
    </>
)
   
};
export default App;
